import React, { useState, useEffect } from 'react';
import { getQuestions } from './grpcClient';

const App = () => {
  const [title, setTitle] = useState('');
  const [type, setType] = useState('');
  const [questions, setQuestions] = useState([]);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const pageSize = 10;

  const fetchQuestions = async () => {
    try {
      getQuestions(title, type, (err, response) => {
        if (err) {
          setError(err.message);
        } else {
          setQuestions(response.questionsList);
          setError(null);
        }
      });
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, [page]);

  const handleNextPage = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handlePreviousPage = () => {
    setPage((prevPage) => Math.max(prevPage - 1, 1));
  };

  const handlePageClick = (pageNumber) => {
    setPage(pageNumber);
  };

  const totalPages = Math.ceil(questions.length / pageSize);
  const paginatedQuestions = questions.slice((page - 1) * pageSize, page * pageSize);

  const renderPagination = () => {
    const pages = [];
    const startPage = Math.max(1, page - 2);
    const endPage = Math.min(totalPages, page + 2);

    if (startPage > 1) {
      pages.push(
        <button key={1} onClick={() => handlePageClick(1)} disabled={page === 1}>
          1
        </button>
      );
      if (startPage > 2) {
        pages.push(<span key="start-ellipsis">...</span>);
      }
    }

    for (let i = startPage; i <= endPage; i++) {
      pages.push(
        <button key={i} onClick={() => handlePageClick(i)} disabled={page === i}>
          {i}
        </button>
      );
    }

    if (endPage < totalPages) {
      if (endPage < totalPages - 1) {
        pages.push(<span key="end-ellipsis">...</span>);
      }
      pages.push(
        <button key={totalPages} onClick={() => handlePageClick(totalPages)} disabled={page === totalPages}>
          {totalPages}
        </button>
      );
    }

    return pages;
  };

  return (
    <div>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Enter title"
      />
      <input
        type="text"
        value={type}
        onChange={(e) => setType(e.target.value)}
        placeholder="Enter type"
      />
      <button onClick={fetchQuestions}>Fetch Questions</button>
      {error && <p>Error: {error}</p>}
      <ul>
        {paginatedQuestions.map((question, index) => (
          <li key={index}>{question.title}</li>
        ))}
      </ul>
      <div>
        <button onClick={handlePreviousPage} disabled={page === 1}>Previous</button>
        {renderPagination()}
        <button onClick={handleNextPage} disabled={page === totalPages}>Next</button>
      </div>
    </div>
  );
};

export default App;
