import React, { useState, useEffect } from 'react';
import { getQuestions } from './grpcClient';

const App = () => {
  const [title, setTitle] = useState('');
  const [type, setType] = useState('');
  const [questions, setQuestions] = useState([]);
  const [error, setError] = useState(null);
  const [page, setPage] = useState(1);
  const pageSize = 10;

  const fetchQuestions = async () => {
    try {
      getQuestions(title, type, (err, response) => {
        if (err) {
          setError(err.message);
        } else {
          setQuestions(response.questionsList);
          setError(null);
        }
      });
    } catch (err) {
      setError(err.message);
    }
  };

  useEffect(() => {
    fetchQuestions();
  }, [page]);

  const handleNextPage = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handlePreviousPage = () => {
    setPage((prevPage) => Math.max(prevPage - 1, 1));
  };

  const paginatedQuestions = questions.slice((page - 1) * pageSize, page * pageSize);

  return (
    <div>
      <input
        type="text"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        placeholder="Enter title"
      />
      <input
        type="text"
        value={type}
        onChange={(e) => setType(e.target.value)}
        placeholder="Enter type"
      />
      <button onClick={fetchQuestions}>Fetch Questions</button>
      {error && <p>Error: {error}</p>}
      <ul>
        {paginatedQuestions.map((question, index) => (
          <li key={index}>{question.title}</li>
        ))}
      </ul>
      <button onClick={handlePreviousPage} disabled={page === 1}>Previous</button>
      <button onClick={handleNextPage}>Next</button>
    </div>
  );
};

export default App;
