import React, { useState } from 'react';
import styles from './QuestionCard.module.css';

const QuestionCard = ({ question }) => {
  const [selectedOption, setSelectedOption] = useState(null);

  const handleOptionClick = (option) => {
    setSelectedOption(option);
  };

  const getOptionClass = (option) => {
    if (selectedOption === option) {
      return option.iscorrectanswer ? styles.selected : styles.incorrect;
    }
    return '';
  };

  if (question.type === 'MCQ') {
    return (
      <div className={styles.questionContainer}>
        <div className={styles.cardWrapper}>
          <div className={styles.highlightBox}>
            <div className={styles.questionText}>
              <h3>{question.title}</h3>
              <p>{question.type}</p>
              <ul>
                {question.optionsList && question.optionsList.map((option, index) => (
                  <li
                    key={index}
                    onClick={() => handleOptionClick(option)}
                    className={getOptionClass(option)}
                  >
                    {option.text}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  } else if (question.type === 'ANAGRAM') {
    return (
      <div className={styles.questionContainer}>
        <div className={styles.cardWrapper}>
          <div className={styles.highlightBox}>
            <div className={styles.questionText}>
              <h3>{question.title}</h3>
              <p>{question.type}</p>
              <ul>
                {question.blocksList && question.blocksList.map((block, index) => (
                  <li key={index}>{block.text}</li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    );
  } else {
    return (
      <div className={styles.questionContainer}>
        <div className={styles.cardWrapper}>
          <div className={styles.highlightBox}>
            <div className={styles.questionText}>
              <h3>Unknown Question Type</h3>
              <p>{question.title}</p>
            </div>
          </div>
        </div>
      </div>
    );
  }
};

export default QuestionCard;
